import { useEffect, useState } from "react"

const Chat = ({socket, name, surname, room}) => {
    const [message, setMessage] = useState('');
    const [messageList, setMessageList] = useState([]);

    useEffect(() => {

    }, [socket]);

    const sendMessage = async () => {
        const messageContent = {
            name: name,
            surname: surname,
            message: message,
            room: room,
            date: (new Date(Date.now).getHours) + ':' + (new Date(Date.now).getMinutes)  
        }

        await socket.emit('message', messageContent);
        setMessage('');
    };

    return (
        <div className='flex items-center justify-center h-full'>
            <div className='w-2/4 h-3/4 bg-orange-200 rounded shadow-2xl relative'>
                <div className="p-2 w-full bg-orange-900 flex items-center rounded-t">
                    <img src="https://static.vecteezy.com/system/resources/previews/000/439/863/non_2x/vector-users-icon.jpg" alt="photo" className='bg-white w-12 h-12 rounded-full bg-contain' />
                    <span className="mx-5 text-gray-200">Username</span>
                    <span className="mx-2 text-gray-200">Surname</span>
                </div>

                <div className="w-full overflow-auto h-[450px]  mr-10 p-3">

                    <div className="w-1/2 p-2 bg-green-600 text-gray-200 rounded-xl rounded-bl-none">
                        <span className="block">lorem300</span>
                        <span className="flex justify-end text-xs">Username Surname</span>
                    </div>

                    <div className="flex justify-end">
                        <div className="w-1/2 mt-3 p-2 bg-blue-600 text-gray-200 rounded-xl rounded-br-none">
                            <span className="block">lorem300</span>
                            <span className="flex justify-end text-xs">Username Surname</span>
                        </div>
                    </div>

                </div>

                <div className="absolute bottom-0 left-0 w-full">
                    <input value={message} onChange={e => setMessage(e.target.value)} className='p-2 w-3/4 rounded-l outline-none' type="text" placeholder='Message...' />
                    <button onClick={sendMessage} className='p-2 w-1/4 rounded-r text-center bg-orange-900 text-gray-200 hover:opacity-80'>Send</button>
                </div>
            </div>
        </div>
    )
}

export default Chat
